import React from "react";
import { useNft } from "use-nft"

function NFT({contractAddress, name, size, tokenId}) {
  const { loading, error, nft } = useNft(contractAddress, tokenId)

  if (error) return <div></div>;

  return (
    <a className={`block h-${size} w-${size} mr-4`} href={`https://opensea.io/assets/${contractAddress}/${tokenId}`}>
      <img src={nft.image} alt={`${nft.name} Asset`} />
      <h1>{`${name ?? "Token"} #${tokenId}`}</h1>
    </a>
  )
}

export default NFT;
