import React, { useRef } from "react";
import { ApolloProvider } from "@apollo/client";
import { createClient } from "./utilities/trellis/apolloClient";
import AppState from "./AppState";

function Wrapper() {
  const trellisClient = useRef(createClient());

  return (
    <div className="flex flex-col justify-center h-screen overflow-x-hidden w-full text-neutral-gray1 overflow-hidden" style={{maxHeight: "-webkit-fill-available"}}>
      <ApolloProvider client={trellisClient.current.client}>
        {console.log("App Rerender")}
        <AppState
          socket={trellisClient.current.absintheSocket}
          phoenixSocket={trellisClient.current.phoenixSocket}/>
      </ApolloProvider>
    </div>
  );
}

export default Wrapper;
