import React, { useEffect, useState } from "react";
import { ethers } from "ethers";

function ENSInfo({number}) {
  const [ address, setAddress ] = useState(null);

  useEffect(async () => {
    await ethers.getDefaultProvider().getResolver(`${number}.eth`).then(async result => {
      setAddress(result.address)
    })
  }, [ethers, setAddress])

  return (
    <div className="text-center w-full text-lg p-8 bg-gray-100 rounded-lg my-8">
      Address: {address}
    </div>
  );
}

export default ENSInfo;
