import React, { useState } from "react";
import { ConnectWallet, Provider, NETWORKS } from '@web3-ui/core';
import Wrapper from "./Wrapper";

function App() {
  const [ number, setNumber ] = useState(6939);

  const handleChange = (event) => {
    setNumber(event.target.value);
  }

  return (
    <Provider network={NETWORKS.mainnet}>
      <div className="flex flex-col items-center justify-center bg-brand-gradient w-full p-8">
        <nav className="flex flex-row mb-8 justify-between w-full items-center">
          <div className="flex flex-row items-center">
            <h2 className="text-6xl text-white font-bold">10K DIGITS</h2>
            <input className="ml-8 p-2 rounded" type="text" value={number} onChange={handleChange} />
          </div>
          <ConnectWallet />
        </nav>
        <Wrapper number={number}/>
      </div>
    </Provider>
  );
}

export default App;
