import React, { useState } from "react";
import { ethers } from "ethers";
import { ConnectWallet, Provider, NETWORKS } from '@web3-ui/core';
import { NftProvider } from "use-nft"
import Wrapper from "./Wrapper";

const fetcher = ["ethers", { ethers, provider: ethers.getDefaultProvider() }]

function App() {
  const [ number, setNumber ] = useState(2815);

  const handleChange = (event) => {
    setNumber(event.target.value);
  }

  return (
    <NftProvider fetcher={fetcher}>
      <Provider network={NETWORKS.mainnet}>
        <div className="flex flex-col items-center justify-start bg-brand-gradient w-full min-h-screen h-full p-8">
          <nav className="flex flex-row mb-8 justify-between w-full items-center">
            <div className="flex flex-row items-center">
              <h2 className="bebas text-6xl text-white font-bold">10K</h2>
              <input className="ml-8 p-2 rounded" placeholder="Lookup Number" type="text" onChange={handleChange} />
            </div>
            <ConnectWallet />
          </nav>
          <Wrapper number={number}/>
          <h2 className="fixed bottom-2 text-white text-center text-sm py-8">built with love by <a className="border-b border-white hover:border-transparent cursor-pointer" href="https://twitter.com/derekbrown" rel="noreferrer" target="_blank">2815.eth</a> for 10KClub</h2>
        </div>
      </Provider>
    </NftProvider>
  );
}

export default App;
